const m1 = $('Check Cluster M1').first();
const m2 = $('Check Cluster M2').first();
const m3 = $('Check Cluster M3').first();
const sys = $('System Info').first();

const m1Online = m1.json?.data ? true : false;
const m2Online = m2.json?.data ? true : false;
const m3Online = m3.json?.data ? true : false;
const online = [m1Online, m2Online, m3Online].filter(Boolean).length;

const now = new Date();
const report = [
  `=== JARVIS RAPPORT MATIN ${now.toLocaleDateString('fr-FR')} ===`,
  '',
  `CLUSTER IA: ${online}/3 nodes`,
  `  M1 (qwen3-30b, 5GPU/43GB): ${m1Online ? 'ONLINE' : 'OFFLINE'}`,
  `  M2 (gpt-oss-20b, 3GPU/24GB): ${m2Online ? 'ONLINE' : 'OFFLINE'}`,
  `  M3 (mistral-7b, 2GPU/16GB): ${m3Online ? 'ONLINE' : 'OFFLINE'}`,
  '',
  `SYSTEME: ${sys.json?.stdout || 'N/A'}`,
  '',
  `TRADING: MEXC Futures | Levier 10x | TP 0.4% | SL 0.25%`,
  `  Paires: BTC ETH SOL SUI PEPE DOGE XRP ADA AVAX LINK`,
  '',
  `JARVIS v10.1 | 69 tools | 295 cmds | 66 skills | 5 agents`,
].join('\n');

return [{ json: { report, online, timestamp: now.toISOString() } }];
